<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.1//EN" "http://www.w3.org/TR/xhtml11/DTD/xhtml11.dtd"><html xmlns="http://www.w3.org/1999/xhtml"> <head> 	<meta http-equiv='Content-Type' content='application/xhtml+xml; charset=utf-8' /> 	<title>PumpkinEngine Development updates</title> 	<link rel="stylesheet" type="text/css" href="<?=base_url()?>content/css/main.css" />	<script type="text/javascript" src="<?=base_url()?>content/js/jquery.js"></script> 	<script type="text/javascript" src="<?=base_url()?>content/js/main.js"></script> 	<script type="text/javascript" src="<?=base_url()?>content/js/jquery.textarea-expander.js"></script></head><body> 	<div id="container">		<div id="header">			<div class="header_body">				Записи разработчиков				<span class="right">					<?if($secure):?>						[ <?=$this->session->userdata('userName');?> ]						<span class="light">|</span>						<a href="<?=base_url()?>logout/">Выход</a>					<?else:?>						<a href="<?=base_url()?>login/">Вход</a>					<?endif?>				</span>			</div>		</div>		<?if($secure):?>			<script type="text/javascript">				function postOpinion()				{					document.form_tick.submit();				}			</script>			<?=form_open(base_url().'updates/add', 'name="form_tick"')?>				<div id="main-posts">					<div id="submit">						<div class="header"></div>						<div class="post_body"><textarea name="updateContent" rows="2" cols="40" onkeypress="if (event.keyCode==10 || (event.ctrlKey &amp;&amp; event.keyCode==13)) {postOpinion()}"></textarea></div>						<div class="footer"></div>					</div>				</div>			<?=form_close()?>		<?endif?>		<?if(isset($updates) && is_array($updates) && count($updates)):?>			<?foreach($updates as $update):?>				<div id="main-posts">					<div id="dev-post">						<div class="header">							<div class="header_text">								<?								if($this->user_model->Secure(array('userType' => 'admin')))								{									echo anchor('delete/'.$update->updateId, ' ', array('class'=>'delete_post_icon'));								}								?>								<span class="left"><a class="dev_usr" href=""><?=$update->userName?></a><span class="light">|</span> <?=$update->userRank?></span>								<span class="edit_buttons right"><a href="/post/161" class="time"><?=$this->update_model->Interval($update->updateDate)?></a></span>							</div>						</div>						<div class="post_body"><?=$update->updateContent?></div>						<div class="footer"></div>					</div>				</div>			<?endforeach?>		<?else:?>			<div id="main-posts">				<div id="dev-post">					<div class="header"></div>					<div class="post_body">Извените но тут ничего нет</div>					<div class="footer"></div>				</div>			</div>		<?endif?>		<div id="pagination">			<? foreach($pagination as $value) echo $value; ?>		</div>	</div>	<div id="footer">		<div class="container">			<span class="left">&copy; 2010 <strong>PumpkinEngine</strong>, <strong>Pumpkin Team</strong>. Все права защищены.</span>			<span class="right">				<div class="left">					<a href="http://pumpkinengine.ru">Назад на <strong>сайт</strong></a>					<span class="light"> | </span>				</div>				&nbsp;									<!--Openstat-->					<span id="openstat2116245"></span><script type="text/javascript">						var openstat = { counter: 2116245, image: 5061, next: openstat }; document.write(unescape("%3Cscript%20src=%22http" + (("https:" == document.location.protocol) ? "s" : "") + "://openstat.net/cnt.js%22%20defer=%22defer%22%3E%3C/script%3E"));					</script>					<!--/Openstat--> 				&nbsp;				<div class="right"><a href="#header"><div class="top_of_page"></div></a></div>			</span>		</div>	</div></body></html>